import api from "./baseApi";

export const eventApi = {
  getListEvents(params, cancelToken) {
    return api.get("/api/admin/events", { params, cancelToken });
  },
  getCmtsEvent(params) {
    return api.get("/api/events/comments", { params });
  },
  getUsersJoinEvent(eventId, status) {
    return api.get(`/api/events/${eventId}/participate`, { status });
  },
  getDetailEvents(params) {
    return api.get(`/api/events/${params.id}`);
  },
  postEvent(params) {
    return api.post("/api/events", params);
  },
  potUserEventJoin(params) {
    return api.post(`/api/user-events/draw-join/${params.id}`, params);
  },
  editEvent(params, id) {
    return api.put(`/api/events/${id}`, params);
  },
  deleteEvent(id, type) {
    return api.delete(`/api/events/${id}`, { data: { type } });
  },
  deleteCmtEvent(cmtId) {
    return api.delete(`/api/events/comments/${cmtId}`);
  },
};
