import { Button, Card, Form, Row, message } from "antd";
import { useForm, useWatch } from "antd/es/form/Form";
import React, { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";

import PATH_URL from "@common/config/pathURL";
import { COLDEF, TYPE_FIELD } from "@common/constant/form";
import { renderFieldForm } from "@common/utils/renderFieldForm";
import { usertagApi } from "@services/apis/usertag";
import { validate } from "@validate/index";
import { useLocation, useNavigate } from "react-router-dom";

function UserTagForm() {
  const { t } = useTranslation();
  const [form] = useForm();
  const { state } = useLocation();
  const isDetailPage = window.location.href.includes("detail");
  const [loading, setLoading] = useState(false);
  const [dataColor, setDataColor] = useState(state?.color || "");
  const listUserSelect = useWatch("userId", form);
  const navigate = useNavigate();

  const initialValues = {
    ...state,
    userId: state?.users?.map((item) => {
      return { key: item?.id, value: item?.id, label: item?.username };
    }),
    color: state?.color || "",
  };

  const handleCancel = () => {
    navigate(PATH_URL.USERTAG_MANAGERMENT.INDEX);
  };

  const showTitle = () => {
    return state
      ? t("usertagManagement.titleEdit")
      : t("usertagManagement.titleCreate");
  };

  const dataForm = [
    {
      colwidth: COLDEF,
      type: TYPE_FIELD.INPUT,
      name: "name",
      maxLength: 20,
      label: t("usertagManagement.list.title"),
      readOnly: isDetailPage,
      style: { textTransform: "uppercase" },
      rules: [
        {
          required: true,
          message: "",
        },
        {
          max: 20,
          message: t("validate.maxCharacter", { number: 20 }),
        },
        {
          validator: (rule, value) => {
            if (!value || value.trim() === "") {
              return Promise.reject(
                t("validate.notEmpty", {
                  fieldName: t("usertagManagement.list.title"),
                }),
              );
            }

            const pattern = /^[A-Za-z\u3040-\u30FF\u4E00-\u9FFF\s]+$/;
            if (!pattern.test(value)) {
              return Promise.reject(t("validate.msgNoNumberNoCharacters"));
            }
            return Promise.resolve();
          },
        },
      ],
    },
    {
      type: TYPE_FIELD.COLOR_PICKER,
      name: "color",
      label: t("usertagManagement.list.color"),
      defaultValue: null,
      disabledAlpha: true,
      setDataColor,
      rules: [
        {
          required: true,
          message: "",
        },
        validate.empty(
          t("validate.notEmpty", {
            fieldName: t("usertagManagement.list.color"),
          }),
        ),
      ],
    },
  ];

  const handleFinish = async (values) => {
    if (state) {
      try {
        setLoading(true);
        await usertagApi.putUserTag(state?.id, {
          ...values,
          name: values.name.toUpperCase(),
          color: dataColor,
        });
        message.success(t("usertagManagement.message.editSuccess"));
        handleCancel();
      } catch (error) {
        message.error(t("usertagManagement.message.editError"));
      } finally {
        setLoading(false);
      }
    } else {
      try {
        setLoading(true);
        await usertagApi.postUserTag({
          ...values,
          name: values.name.toUpperCase(),
          color: dataColor,
        });
        message.success(t("usertagManagement.message.createSuccess"));
        handleCancel();
      } catch (error) {
        message.error(t("usertagManagement.message.createError"));
        message.error(error);
      } finally {
        setLoading(false);
      }
    }
  };

  useEffect(() => {
    form.validateFields(["userId"]);
  }, [listUserSelect]);

  return (
    <div className="course-form form-default">
      <Row align="middle" className="full-width header-table">
        <h3>{showTitle()}</h3>
      </Row>

      <Card>
        <Form
          form={form}
          colon={false}
          layout="vertical"
          initialValues={state ? initialValues : {}}
          onFinish={handleFinish}
          autoComplete="off"
        >
          <Row gutter={[48, 8]}>{renderFieldForm(dataForm)}</Row>
          <Row className="flex jc-center gap-20 mt-15 full-width">
            <Button
              type="default"
              className="fw-600 cancel-btn"
              onClick={handleCancel}
            >
              {t("form.btnBack")}
            </Button>
            <Button
              loading={loading}
              disabled={loading}
              className="fw-600 submit-btn"
              type="primary"
              htmlType="submit"
            >
              {state ? t("form.btnEdit") : t("form.btnCreate")}
            </Button>
          </Row>
        </Form>
      </Card>
    </div>
  );
}

UserTagForm.propTypes = {};

export default UserTagForm;
