import api from "./baseApi";

export const courseApi = {
  getCourses(params, cancelToken) {
    return api.get("/api/courses", { params, cancelToken });
  },
  postCourse(params) {
    return api.post("/api/courses", params);
  },
  getDetailCourse(id) {
    return api.get(`/api/courses/${id}`);
  },
  editCourse(id, params) {
    return api.put(`/api/courses/${id}`, params);
  },
  delete(courseId) {
    return api.delete(`/api/courses/${courseId}`);
  },

  getBadges(params, cancelToken) {
    return api.get("/api/badge-course", { params, cancelToken });
  },
  postBadge(params) {
    return api.post("/api/badge-course", params);
  },
  getDetailBadge(id) {
    return api.get(`/api/badge-course/${id}`);
  },
  editBadge(id, params) {
    return api.put(`/api/badge-course/${id}`, params);
  },
  deleteBadge(badgeId) {
    return api.delete(`/api/badge-course/${badgeId}`);
  },

  getListCheckin(params, cancelToken) {
    return api.get("/api/statistics-checkin", { params, cancelToken });
  },
};
