import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { Button, Card, Form, message, Row } from "antd";
import { t } from "i18next";

import TextField from "@components/form/MyInputField";
import { authApi } from "@services/apis/auth";

function FormVerifyOtp() {
  const navigate = useNavigate();
  const { state } = useLocation();

  const handleBack = () => {
    navigate("..");
  };

  const handleFinish = (values) => {
    const payload = {
      email: values?.email?.trim(),
      otpCode: parseFloat(values?.otpCode),
      tokenDevice: "",
    };

    authApi
      .verify(payload)
      .then(() => {
        navigate("..");
        message.success("xác thực thành công");
      })
      .catch(() => {
        message.error("xác thực thất bại");
      });
  };

  return (
    <div>
      <Row align="middle" className="full-width header-table">
        <h1>{t("managementUser.verifyOtp")}</h1>
      </Row>
      <Card>
        <h1 className="mb-20">{t("managementUser.otpAlert")}</h1>
        <Form
          labelCol={{
            span: 7,
          }}
          wrapperCol={{
            span: 12,
          }}
          colon={false}
          initialValues={{ email: state }}
          onFinish={handleFinish}
        >
          <TextField
            label={t("managementUser.form.email")}
            name="email"
            readOnly
          />
          <TextField label={t("managementUser.form.otpCode")} name="otpCode" />
          <div className="flex jc-center gap-20">
            <Button type="default" onClick={handleBack}>
              {t("managementUser.form.btnBack")}
            </Button>
            <Button type="primary" htmlType="submit">
              {t("managementUser.form.btnCreate")}
            </Button>
          </div>
        </Form>
      </Card>
    </div>
  );
}

FormVerifyOtp.propTypes = {};

export default FormVerifyOtp;
