import React from "react";
import PATH_URL from "@common/config/pathURL";
import ReactLinkify from "react-linkify";

import { Button, Row } from "antd";
import { useTranslation } from "react-i18next";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { formatDateTimeTable } from "@common/utils/dateHelper";
import { DATE_FORMAT_DISPLAY } from "@common/constant/date";
import { IMAGE_URL } from "@common/config/endpoint";
import useRenderForm from "@common/hooks/useRenderFormDetail";
import { formateValue } from "@common/utils/format";

function CollectionDetail() {
  const { t } = useTranslation();
  const { state } = useLocation();
  const navigate = useNavigate();

  const handleRenderItem = (datas, keyRender, link) => {
    if (!datas.length) return "-";

    return datas.map((data, key) =>
      !link ? (
        <div key={key}>{data[keyRender]}</div>
      ) : (
        <div key={key}>
          <Link to={link} state={{ id: data.id }}>
            {data[keyRender]}
          </Link>
        </div>
      ),
    );
  };

  const columnsData = [
    {
      title: "",
      src: `${IMAGE_URL}${state?.images}/original.jpg`,
      propertyImg: { height: 200, width: 200 },
    },
    {
      title: t("managementCollections.list.nameCollection"),
      text: formateValue(state?.title),
      column: 12,
    },
    {
      title: t("managementCollections.list.createdAt"),
      text: formateValue(
        formatDateTimeTable(state?.createdAt, DATE_FORMAT_DISPLAY),
      ),
      column: 12,
    },
    {
      title: t("managementCollections.list.content"),
      text: formateValue(state?.description),
      column: 12,
    },
    // type TOP ROUTES
    {
      title: t("managementCollections.list.totalRoute"),
      text: formateValue(state?.courseCount),
      hidden: state?.courseCount || state?.checkPoints?.length,
      column: 12,
    },
    {
      title: t("managementCollections.list.totalCheckpoint"),
      text: formateValue(`${state?.checkPoints?.length}`),
      hidden: state?.courseCount || state?.checkPoints?.length,
      column: 12,
    },
    {
      title: t("managementCollections.list.routeInCollection"),
      content: handleRenderItem(
        state?.courses,
        "title",
        `${PATH_URL.MANAGER_COURSE.INDEX}/${PATH_URL.MANAGER_COURSE.DETAIL}`,
      ),
      hidden: state?.courseCount || state?.checkPoints?.length,
      column: 12,
    },
    {
      title: t("managementCollections.list.checkpointInCollection"),
      content: handleRenderItem(
        state?.checkPoints,
        "name",
        `${PATH_URL.CHECKPOINT.INDEX}/${PATH_URL.CHECKPOINT.DETAIL}`,
      ),
      hidden: state?.courseCount || state?.checkPoints?.length,
      column: 12,
    },
    // type GUIDES
    {
      title: t("managementCollections.list.link"),
      link: state?.link ? <a href={state?.link}>{state?.link}</a> : "-",
      hidden: !state?.courseCount && !state?.checkPoints?.length,
      column: 12,
    },
  ];

  const columns = useRenderForm(columnsData);

  return (
    <div className="course-form form-default">
      <Row align="middle" className="full-width header-table">
        <h1>{t("managementCollections.titleDetail")}</h1>
      </Row>

      <Row gutter={[34]}>{columns}</Row>

      <Row className="mt-20 jc-center">
        <Button className="ml-10 mr-10" onClick={() => navigate("..")}>
          {t("managementCollections.form.btnBack")}
        </Button>
        <Button
          type="primary"
          className="ml-10 mr-10"
          onClick={(e) => {
            e.stopPropagation();
            navigate(`../${PATH_URL.MANAGER_COLLECTION.EDIT}`, {
              state,
            });
          }}
        >
          {t("managementCollections.form.btnEdit")}
        </Button>
      </Row>
    </div>
  );
}

export default CollectionDetail;
