import React, { useEffect, useState } from "react";
import { TYPE_CHECKPOINT } from "@common/constant/masterData";
import { checkPointApi } from "@services/apis/checkpoint";
import PropTypes from "prop-types";
import { useLocation } from "react-router-dom";
import CheckPointForm from "./CheckPointForm";
import DetailData from "./DetailData";

function CheckPointDetail(props) {
  const { isEditMode } = props;
  const location = useLocation();
  const [checkPoint, setCheckPoint] = useState();
  const [value, setValue] = useState();
  const [isEditCheckPoint, setIsEditCheckPoint] = useState(false);
  const formatDataDetail = {
    ...location.state,
    course: location.state?.course?.title,
    type: TYPE_CHECKPOINT[location.state?.type]?.label,
  };

  const getCheckPointDetail = async () => {
    const res = await checkPointApi.getCheckPoint({ id: location.state.id });
    setCheckPoint(res);
  };

  const showEditForm = () => {
    setIsEditCheckPoint(true);
  };

  useEffect(() => {
    setIsEditCheckPoint(false);
    getCheckPointDetail();
  }, [value]);

  return (
    <div>
      {isEditCheckPoint ? (
        <CheckPointForm isCreate={false} data={checkPoint} />
      ) : (
        <DetailData showEditForm={showEditForm} data={formatDataDetail || {}} />
      )}
    </div>
  );
}
CheckPointDetail.propTypes = {
  isEditMode: PropTypes.bool,
};

export default CheckPointDetail;
