import React from "react";
import PropTypes from "prop-types";
import { Button, Row } from "antd";
import { t } from "i18next";
import { useLocation, useNavigate } from "react-router-dom";

import useRenderForm from "@common/hooks/useRenderFormDetail";
import { formateValue } from "@common/utils/format";
import PATH_URL from "@common/config/pathURL";
import dayjs from "dayjs";
import { formatDateTimeTable } from "@common/utils/dateHelper";

function MailDetail() {
  const { state } = useLocation();
  const navigate = useNavigate();
  // const allowEdit = dayjs(state?.sendAt).isAfter(dayjs(), "minute");

  const columnData = [
    {
      title: t("mail.form.name"),
      text: formateValue(state?.name),
      column: 12,
    },
    {
      title: t("mail.form.email"),
      text: formateValue(state?.email),
      column: 12,
    },
    {
      title: t("mail.form.title"),
      text: formateValue(state?.title),
      column: 12,
    },
    {
      title: t("mail.form.content"),
      text: formateValue(state?.body),
      column: 12,
    },
    {
      title: t("mail.form.sendDate"),
      text: formateValue(formatDateTimeTable(state?.sendAt)),
      column: 12,
    },
  ];
  const columns = useRenderForm(columnData);

  return (
    <div className="course-form form-default">
      <Row align="middle" className="full-width header-table">
        <h1>{t("mail.detail")}</h1>
      </Row>

      <Row className="" gutter={[34]}>
        {columns}
      </Row>

      <Row className="mt-20 jc-center">
        <Button className="ml-10 mr-10" onClick={() => navigate("..")}>
          {t("events.form.btnBack")}
        </Button>
        {/* {allowEdit && (
          <Button
            type="primary"
            className="ml-10 mr-10"
            onClick={(e) => {
              e.stopPropagation();
              navigate(`../${PATH_URL.SEND_MAIL.EDIT}`, {
                state,
              });
            }}
          >
            {t("events.form.btnEdit")}
          </Button>
        )} */}
      </Row>
    </div>
  );
}

MailDetail.propTypes = {};

export default MailDetail;
