import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { useLocation, useNavigate } from "react-router-dom";
import { Button, Col, Divider, Image, Row } from "antd";

import PATH_URL from "@common/config/pathURL";
import { IMAGE_URL } from "@common/config/endpoint";
import { useTranslation } from "react-i18next";
import { checkPointApi } from "@services/apis/checkpoint";
import { TYPE_CHECKPOINT } from "@common/constant/masterData";
import { formateValue } from "@common/utils/format";
import useRenderForm from "@common/hooks/useRenderFormDetail";

function DetailData(props) {
  const { showEditForm } = props;
  const { t } = useTranslation();
  const [checkpoint, setCheckpoint] = useState();
  const [carousel, setCarousel] = useState(0);

  const location = useLocation();
  const navigate = useNavigate();
  const handleRedirectListPage = () => {
    navigate(PATH_URL.CHECKPOINT.INDEX);
  };

  const getCheckPointDetail = async () => {
    const res = await checkPointApi.getCheckPoint({ id: location.state.id });
    setCheckpoint(res);
  };

  const handlePrev = () => {
    setCarousel((prevState) => prevState - 1);
  };

  const handleNext = () => {
    setCarousel((prevState) => prevState + 1);
  };

  useEffect(() => {
    if (!location.state.id) navigate("..");

    getCheckPointDetail();
  }, [location.state.id]);

  const columnsData = [
    {
      title: t("checkpoint.form.name"),
      text: formateValue(checkpoint?.name),
      column: 12,
    },
    {
      title: t("checkpoint.form.place"),
      text: formateValue(checkpoint?.place),
      column: 12,
    },
    {
      title: t("checkpoint.form.type"),
      text: formateValue(TYPE_CHECKPOINT[checkpoint?.type]?.label),
      column: 12,
    },
    {
      title: t("checkpoint.form.description"),
      text: formateValue(checkpoint?.description),
      column: 12,
    },
    {
      title: t("checkpoint.form.distance"),
      text: formateValue(checkpoint?.distance),
      column: 12,
    },
    {
      title: t("checkpoint.form.height"),
      text: formateValue(checkpoint?.height),
      column: 12,
    },
    {
      title: t("checkpoint.form.latitude"),
      text: formateValue(checkpoint?.latitude),
      column: 12,
    },
    {
      title: t("checkpoint.form.longitude"),
      text: formateValue(checkpoint?.longitude),
      column: 12,
    },
    {
      title: t("checkpoint.form.review"),
      text: formateValue(checkpoint?.review),
      column: 12,
    },
  ];

  const columns = useRenderForm(columnsData, checkpoint);

  return (
    <div className="detail">
      <Row align="middle" className="header-table full-width">
        <h1>{t("checkpoint.detail.title")}</h1>
      </Row>

      <Row className="flex-center mb-15">
        <Image
          className="avatar"
          src={`${IMAGE_URL}${checkpoint?.image[carousel]}/original.jpg`}
        />
      </Row>
      {checkpoint?.image && checkpoint.image.length > 1 && (
        <Row className="flex-center mb-15">
          <Button type="primary" disabled={!carousel} onClick={handlePrev}>
            {t("events.form.btnPrev")}
          </Button>
          <Button
            type="primary"
            disabled={carousel === checkpoint.image.length - 1}
            onClick={handleNext}
          >
            {t("events.form.btnNext")}
          </Button>
        </Row>
      )}
      <Divider />

      <Row gutter={[34]}>{columns}</Row>

      <Row className="flex jc-center gap-20 full-width">
        <Button
          onClick={handleRedirectListPage}
          className="fw-600 cancel-btn"
          type="default"
        >
          {t("checkpoint.form.btnBack")}
        </Button>
        <Button
          type="primary"
          onClick={showEditForm}
          className="fw-600 submit-btn"
        >
          {t("checkpoint.form.btnEdit")}
        </Button>
      </Row>
    </div>
  );
}

DetailData.propTypes = {
  showEditForm: PropTypes.func,
};

export default DetailData;
