import React, { useEffect } from "react";
import { Row } from "antd";
import { useLocation, useNavigate } from "react-router-dom";
import { t } from "i18next";
import useRenderForm from "@common/hooks/useRenderFormDetail";
import { IMAGE_DEFAULT } from "@common/constant/masterData";
import { formateValue } from "@common/utils/format";

function AdminDetail() {
  const navigate = useNavigate();
  const { state: userData } = useLocation();
  const columnsData = [
    {
      title: "",
      src: userData?.avatar || IMAGE_DEFAULT,
      propertyImg: { height: 120, width: 120 },
      column: 24,
    },
    {
      title: t("managementAdmin.list.fullName"),
      text: formateValue(userData?.fullname),
      column: 12,
    },
    {
      title: "managementAdmin.list.email",
      text: formateValue(userData?.email),
      column: 12,
    },
    {
      title: t("managementAdmin.list.address"),
      text: formateValue(userData?.address),
      column: 12,
    },
    {
      title: t("managementAdmin.list.type"),
      text: formateValue(userData?.type),
      column: 12,
    },
    {
      title: t("managementAdmin.list.role"),
      text: formateValue(userData?.role?.roleName),
      column: 12,
    },
    {
      title: t("managementAdmin.list.description"),
      text: formateValue(userData?.description),
      column: 12,
    },
  ];
  const columns = useRenderForm(columnsData);

  useEffect(() => {
    if (!userData) {
      navigate("..");
    }
  }, [userData]);

  return (
    <div>
      <Row align="middle" className="header-table full-width">
        <h1>{t("managementAdmin.detailAdmin")}</h1>
      </Row>
      <Row gutter={[34]}>{columns}</Row>
    </div>
  );
}

AdminDetail.propTypes = {};

export default AdminDetail;
