import api from "./baseApi";

export const badgeApi = {
  getBadges(params, cancelToken) {
    return api.get("/api/badge", { params, cancelToken });
  },
  postBadge(payload) {
    return api.post("/api/badge", payload);
  },
  putBadge(id, payload) {
    return api.put(`/api/badge/${id}`, payload);
  },
  deleteBadge(badgeId) {
    return api.delete(`/api/badge/${badgeId}`);
  },

  getAllTypeBadge(cancelToken) {
    return api.get("/api/badge/badge-attach", { cancelToken });
  },
  postBadgeAttach(payload) {
    return api.post("/api/admin/badge-attach", payload);
  },
};
